mapper.select:
SELECT id, title, content, user_id, generation_id, created_at, updated_at, is_deleted
FROM algorithm_guide
WHERE generation_id = #{generationId}
  AND is_deleted = false
ORDER BY created_at DESC
LIMIT #{offset}, #{pageSize}

SELECT id, title, content, user_id, generation_id, created_at, updated_at, is_deleted
FROM algorithm_guide
WHERE generation_id = #{generationId}
  AND (
      CASE #{searchType}
          WHEN 'title' THEN title LIKE CONCAT('%', #{keyword}, '%')
          WHEN 'content' THEN content LIKE CONCAT('%', #{keyword}, '%')
          ELSE false
      END
  )
  AND is_deleted = false
ORDER BY created_at DESC
LIMIT #{offset}, #{pageSize}

mapper.delete:
UPDATE algorithm_guide
SET is_deleted = 1
WHERE id = #{id}
