mapper.select:
SELECT s.id as solutionId, s.title, u.name as userName, s.runtime, s.memory, p.name AS languageName CASE WHEN b.id IS NOT NULL THEN true ELSE false END as isBookmarked FROM algorithm_problem_solution s INNER JOIN user u ON s.user_id = u.id INNER JOIN programming_language p ON s.language_id = p.id LEFT JOIN bookmark b ON b.content_id = s.id AND b.content_type = 'algo_solution' AND b.user_id = #{userId} WHERE s.is_deleted = false AND s.problem_id = #{problemId}
AND u.generation_id = #{generationId}
AND p.name IN
#{language}
ORDER BY
s.runtime ASC
s.memory ASC
s.created_at DESC
LIMIT 9 OFFSET #{offset}

SELECT COUNT(*)
FROM algorithm_problem_solution s
INNER JOIN user u ON s.user_id = u.id
INNER JOIN programming_language p ON s.language_id = p.id
LEFT JOIN bookmark b ON b.content_id = s.id 
    AND b.content_type = 'algo_solution'
    AND b.user_id = #{userId}
WHERE s.is_deleted = false
AND s.problem_id = #{problemId}

    AND u.generation_id = #{generationId}


    AND p.name IN

        #{language}
