mapper.select:
SELECT s.id, s.language_id, s.memory, s.runtime, s.title, u.name AS subtitle, p.name AS languageName FROM algorithm_problem_solution s INNER JOIN user u ON s.user_id = u.id INNER JOIN programming_language p ON s.language_id = p.id WHERE s.is_deleted = false AND s.problem_id = #{problemId}
AND s.generation_id = #{generationId}
AND p.name IN
#{language}
ORDER BY
s.runtime ASC
s.memory ASC
s.id DESC
LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*)
FROM algorithm_problem_solution s
INNER JOIN user u ON s.user_id = u.id
INNER JOIN programming_language p ON s.language_id = p.id
WHERE s.is_deleted = false
AND s.problem_id = #{problemId}

    AND s.generation_id = #{generationId}


    AND p.name IN

        #{language}
