mapper.select:
SELECT id as scheduleId, title, start_date, end_date, schedule_type, is_public AS publicAccess, generation_id, user_id FROM schedule WHERE 1=1 AND YEAR(start_date) = #{year} AND MONTH(start_date) = #{month}
AND generation_id = #{generationId} AND is_public = true
ORDER BY start_date DESC

SELECT id AS trackProjrctId,
       title,
       created_at AS createdAt,
	   generation_id,
	   user_id
FROM track_project
WHERE YEAR(created_at) = #{year}
  AND MONTH(created_at) = #{month}
  AND generation_id = #{generationId}
  AND is_deleted = false
