mapper.select:
SELECT id, title, objective, description, created_at
FROM track_project
WHERE id = #{trackProjectId}

mapper.delete:
DELETE FROM track_excluded_member
WHERE project_id = #{trackProjectId}
AND user_id IN

    #{memberId}

mapper.select:
SELECT user_id
FROM track_excluded_member
WHERE project_id = #{trackProjectId}

mapper.update:
UPDATE track_project
SET is_deleted = true
WHERE id = #{trackProjectId}
