mapper.select:
WITH NoTeamUsers AS ( SELECT u.id, u.name FROM user u WHERE u.generation_id = ( SELECT generation_id FROM track_project WHERE id = #{trackProjectId} ) AND u.id NOT IN ( SELECT tm.user_id FROM track_team_member tm JOIN track_team t ON tm.track_team_id = t.id WHERE t.track_project_id = #{trackProjectId} ) AND u.id NOT IN ( SELECT user_id FROM track_excluded_member WHERE project_id = #{trackProjectId} ) ) SELECT /* 프로젝트 정보 */ tp.id AS id, tp.title AS title, tp.objective AS objective, tp.description AS description, tp.created_at AS created_at,
/* 팀이 없는 사용자 */ ntu.id AS no_team_user_id, ntu.name AS no_team_user_name,
/* 팀 정보 */ tt.id AS team_id, tt.is_completed,
/* 팀 멤버 정보 */ u.id AS member_id, u.name AS member_name, tm.role AS member_role,
/* 게시글 정보 */ pb.id AS board_id, pb.title AS board_title, pb.created_at AS board_created_at, pb.thumbnail AS board_thumbnail
FROM track_project tp LEFT JOIN NoTeamUsers ntu ON 1=1 LEFT JOIN track_team tt ON tp.id = tt.track_project_id LEFT JOIN track_team_member tm ON tt.id = tm.track_team_id LEFT JOIN user u ON tm.user_id = u.id LEFT JOIN track_project_board pb ON tt.id = pb.track_team_id WHERE tp.id = #{trackProjectId} AND tp.is_deleted = 0 ORDER BY tt.id, tm.id, pb.created_at DESC

mapper.delete:
DELETE FROM track_excluded_member
WHERE project_id = #{trackProjectId}
AND user_id IN

    #{memberId}

mapper.select:
SELECT user_id
FROM track_excluded_member
WHERE project_id = #{trackProjectId}

mapper.update:
UPDATE track_project
SET is_deleted = true
WHERE id = #{trackProjectId}

mapper.select:
SELECT EXISTS (
    SELECT 1 
    FROM track_project 
    WHERE id = #{trackProjectId}
    AND is_deleted = false
)
