mapper.insert:
 INSERT INTO track_team (
     generation_id,
     track_project_id
 ) VALUES(
     #{generationId},
     #{trackProjectId}
)

mapper.update:
UPDATE track_team_member
SET
role = CASE

    WHEN track_team_id = #{member.trackTeamId} AND id = #{member.trackMemberId}
    THEN #{member.role}

ELSE role
END,
user_id = CASE

    WHEN track_team_id = #{member.trackTeamId} AND id = #{member.trackMemberId}
    THEN #{member.user.userId}

ELSE user_id
END
WHERE track_team_id IN

    #{member.trackTeamId}

AND id IN

    #{member.trackMemberId}

mapper.select:
SELECT id as userId,
       name
FROM user u
WHERE u.generation_id = #{generationId}
  AND u.is_ssafy = true
  AND u.id NOT IN (
    SELECT user_id
    FROM track_team_member ttm
             JOIN track_team tt ON ttm.track_team_id = tt.id
    WHERE tt.track_project_id = #{projectId}
)
ORDER BY u.name ASC;
