mapper.select:
SELECT EXISTS (
    SELECT 1
    FROM track_team_member tm
             INNER JOIN track_team t ON tm.track_team_id = t.id
    WHERE t.track_project_id = #{trackProjectId}
      AND tm.user_id = #{userId}
)

mapper.delete:
DELETE FROM track_team_member
WHERE track_team_id = #{teamId}
  AND role = 'MEMBER'
