mapper.select:
SELECT id, title, thumbnail, created_at FROM web_guide WHERE is_deleted = false
AND generation_id = #{generationId}
ORDER BY updated_at DESC LIMIT #{offset}, #{pageSize}

SELECT id, title, thumbnail, created_at FROM web_guide WHERE is_deleted = false AND title LIKE CONCAT("%", #{title}, "%")
AND generation_id = #{generationId}
ORDER BY created_at DESC LIMIT #{offset}, #{pageSize}
