mapper.select:
SELECT id, title, url, created_at FROM web_recommend WHERE is_deleted = false
AND generation_id = (SELECT id FROM generation WHERE is_active = true)
ORDER BY created_at DESC LIMIT #{offset}, #{pageSize}

SELECT id, title, url, created_at FROM web_recommend WHERE is_deleted = false
AND generation_id = (SELECT id FROM generation WHERE is_active = true)
AND title LIKE CONCAT('%', #{title}, '%') ORDER BY created_at DESC LIMIT #{offset}, #{pageSize}
